mapper.select:
SELECT SUM(payment) totalValue,DATE_FORMAT(begin_time,'%Y-%m') strignName FROM t_item WHERE DATE_FORMAT(begin_time,'%Y-%m') IN
#{monthvalue}
GROUP BY strignName

SELECT SUM(payment) totalValue, depart_id as intType  FROM t_item
WHERE DATE_FORMAT(begin_time,'%Y') = #{year}
GROUP BY intType

SELECT SUM(payment) totalValue  ,AREA strignName  FROM t_item
WHERE DATE_FORMAT(begin_time,'%Y') = #{year}
GROUP BY strignName

select * from t_item


        and begin_time >= date(#{beginDate})


        and end_time <= date(#{endDate})


        and depart_id = #{departId}



limit #{index},#{pageSize}

SELECT IFNULL(SUM(payment),0) as totalValue,status as intType FROM t_item

    DATE_FORMAT(begin_time,'%Y') =#{year}

       and depart_id =#{departId}


        and status =#{status}


GROUP BY intType

SELECT IFNULL(SUM(payment),0) as totalValue,type as intType FROM t_item

    DATE_FORMAT(begin_time,'%Y') =#{year}

        and depart_id =#{departId}


        and status =#{status}


GROUP BY intType
