mapper.select:
SELECT SUM(base_wage) totalValue,DATE_FORMAT(work_time,'%Y-%m') strignName FROM t_wage WHERE DATE_FORMAT(work_time,'%Y-%m') IN
#{monthvalue}
GROUP BY strignName

SELECT SUM(base_wage) totalValue, depart_id intType FROM t_wage
WHERE DATE_FORMAT(work_time,'%Y-%m') =#{year}
GROUP BY intType

SELECT SUM(w.base_wage)+SUM(e.money) totalValue,DATE_FORMAT(w.work_time,'%Y-%m') strignName FROM t_wage w,t_expense e WHERE DATE_FORMAT(w.work_time,'%Y-%m') = DATE_FORMAT(e.create_time,'%Y-%m') AND DATE_FORMAT(w.work_time,'%Y-%m') IN
#{monthvalue}
AND e.status =1 GROUP BY strignName

SELECT SUM(base_wage)baseWage,SUM(social_pay)socialPay,SUM(accumulation_fund)accumulationFund,SUM(take_pay)takePay,sum(holiday_pay) holidayPay FROM t_wage

    and work_time = date(#{date})

        and depart_id =#{departId}
